                        {
                            global.set("SurveyStep2",2);
                            msg.topic = "Start Delay for next Watering...";
                            msg.delay = global.get("DelayTimePump2")*60;
                            global.set("DelayOfCycle2",0);
                        }
                        else
                        {                    
                            msg.topic = "Pump Running...";
                        }
                    break;
                    // wait for delay between watering act and return to 0
                    // increment step and alarm if problem

                    case 2:  
                        if (global.get("DelayOfCycle2")==1)
                        {
                            global.set("SurveyStep2", 0);
                            global.set("CycleCount2", global.get("CycleCount2")+1);
                            msg.topic = "Survey Soil Moisture...";                        
                        }
                        else
                            msg.topic = "Start Delay for next Watering...";                 
                    break;
                }    
            }
            break;          
        default:
            break;

    }
}
else
{
